import axios from "axios";

axios.defaults.baseURL="http://localhost:8080"
axios.defaults.headers = {"Access-Control-Allow-Origin": "*"}

function verifyLogin(user){
    return axios({
        method: 'post',
        url: '/login',
        data: user
    })
}

function signup(user){
    return axios({
        method: 'post',
        url: '/signup',
        data: user
    })
}

function addBlog(blog){
    return axios({
        method: 'post',
        url: '/blogs/add',
        data: blog
    })
}

function getAllBlogs(){
    return axios({
        method: 'get',
        url: '/blogs'
    })
}



export {verifyLogin,signup,addBlog,getAllBlogs}
