import React, { useState } from 'react';
import { toast } from 'react-toastify';
import { CardHeader, Row } from 'reactstrap';

import 
{
    Card,
    CardBody,
    CardTitle,
    CardSubtitle,
    Container,
    Button,
    Col
}
 from 'reactstrap'
import { getAuthors } from '../Axios/AxiosRequests';

// style={{"width": "50rem"}}

function Blog({blog,history}) {

    let prom = getAuthors()
    let [author,setAuthor]= useState(null)

    function chechAuthor(au){
      console.log(blog.aid)
      console.log(au.uid)
      console.log(au.uid==blog.aid)
      return au.uid==blog.aid
    }

    prom.then(res=>{
      let newauthor = res.data.filter(chechAuthor)[0].userName
      setAuthor(newauthor)
    }).catch(e=>
      console.log(e))

