import { verifyLogin } from "../Axios/AxiosRequests"

const initialState = "abhi"

const usermapper = (state=initialState, action) =>{

    switch(action.type){
        
        case "LOGIN":
            let newState=null
            const cred= localStorage.getItem(userdetails)
            const prom= verifyLogin(this.state.credentials)
            newState=prom.then(res=>{return res.data}).catch(e=>{console.log("user mapper error")})

            return newState


        case "LOGOUT":
            return null

        default: return null
