import React from "react";
import { toast } from "react-toastify";
import { Form, Input, FormGroup, Label, Button, Container } from "reactstrap";
import { signup } from "../Axios/AxiosRequests";

const Signup = (props) => {
  let credentials = {
    userName: "",
    password: "",
    type:"Reader"
  };

  function userNameChangeHandler(e) {
    credentials.userName = e.target.value;
  }

  function passwordChangeHandler(e) {
    credentials.password = e.target.value;
  }

  function typechangeHandler(e){
    credentials.type = e.target.value;
    console.log(credentials)
  }

  function submitHandler(e) {
    const prom = signup(credentials);

    prom
      .then((res) => {
        const success = res.data;
        if (success) {
          toast.success("Signed Up successfully!");
          props.history.push("/login");
        } else {
          toast.error("Username Already Exists!!");
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }

  return (
    <div>
      <Form className="border p-3">
        <FormGroup>
          <Label for="username">Username:</Label>
          <Input
            type="text"
            name="username"
            id="username"
            placeholder="Enter User Name here.."
            onChange={userNameChangeHandler}
          />
        </FormGroup>

        <FormGroup>
          <Label for="password">Password:</Label>
          <Input
            type="text"
            name="password"
            id="password"
            placeholder="Enter password here.."
            onChange={passwordChangeHandler}
          />
        </FormGroup>

        <div class="dropdown">
          <button
            class="btn btn-secondary dropdown-toggle"
            type="button"
            id="type"
            data-toggle="dropdown"
            aria-haspopup="true"
            aria-expanded="false"
            onChange={typechangeHandler}
          >
            User Type
          </button>
          <div class="dropdown-menu" aria-labelledby="dropdownMenu2" onChange={typechangeHandler}>
            <button class="dropdown-item" type="button">
              Super Admin
            </button>
            <button class="dropdown-item" type="button">
              Admin
            </button>
            <button class="dropdown-item" type="button">
              Reader
            </button>
          </div>
        </div>

        <Container className="text-center">
          <Button onClick={submitHandler} color="success">
            Submit
          </Button>
        </Container>
      </Form>
    </div>
  );
};

export default Signup;
