import React, { useState } from 'react';
import { toast } from 'react-toastify';
import { Row } from 'reactstrap';

import 
{
    Card,
    CardBody,
    CardTitle,
    CardSubtitle,
    Container,
    Button,
    Col
}
 from 'reactstrap'
import { getAuthors } from '../Axios/AxiosRequests';

// style={{"width": "50rem"}}

function Blog({blog,history}) {

    let prom = getAuthors()
    let [author,setAuthor]= useState(null)

    function chechAuthor(au){
      console.log(blog.aid)
      console.log(au.uid)
      console.log(au.uid==blog.aid)
      return au.uid==blog.aid
    }

    prom.then(res=>{
      let newauthor = res.data.filter(chechAuthor)[0].userName
      setAuthor(newauthor)
    }).catch

    console.log("new Author name")
    console.log(author)

    return (
        <div className="m-2">
          <Card className="mycard" body color="primary" outline>
            <CardBody className="text-center">
              <CardTitle className="card-title">Author: {author}</CardTitle>
              <CardSubtitle>
              {
                blog.content===null?
                "No Content":
                blog.content
              }
            </CardSubtitle>
            <Button color='warning'>Update</Button>
            <Button color='danger' className='m-3'>Delete</Button>
          </CardBody>
        </Card>
      </div>
    );

}

export default Blog;
